function removeCartItem(e){$.ajax({type:"POST",url:"/remove_from_cart_at_checkout/",data:{item_id:e,csrfmiddlewaretoken:getCSRFToken()},success:function(e){e.success?location.reload():console.error("Failed to remove item from cart.")},error:function(e,t,r){console.error("Error occurred during item removal:",r)}})}function getCSRFToken(){return $('input[name="csrfmiddlewaretoken"]').val()}$(document).ready((function(){$(document).on("click",".bi-trash-fill",(function(){removeCartItem($(this).closest("tr").attr("id").replace("item-",""))})),$(document).on("click",".bi-arrow-counterclockwise",(function(){const e=$(this).closest("tr").attr("id").replace("item-","");restoreCartItem(e)}))}));